import React, { useRef, useEffect, useState } from 'react';
import Webcam from 'react-webcam';
import * as hands from '@mediapipe/hands';
import * as camera from '@mediapipe/camera_utils';
import { drawConnectors, drawLandmarks } from '@mediapipe/drawing_utils';
import { recognizeKodalySign, kodalySigns, analyzeHand } from '../utils/kodalySignsDB';
import './HandDetection.css';

const HandDetection = () => {
    const webcamRef = useRef(null);
    const canvasRef = useRef(null);
    const handsRef = useRef(null);
    const cameraRef = useRef(null);
    const [detectedSign, setDetectedSign] = useState(null);
    const [showInstructions, setShowInstructions] = useState(true);
    const [showDebug, setShowDebug] = useState(false);
    const [debugInfo, setDebugInfo] = useState(null);
    const [cameraReady, setCameraReady] = useState(false);

    useEffect(() => {
        if (!webcamRef.current) return;

        handsRef.current = new hands.Hands({
            locateFile: (file) => {
                return `https://cdn.jsdelivr.net/npm/@mediapipe/hands/${file}`;
            }
        });

        handsRef.current.setOptions({
            maxNumHands: 1,
            modelComplexity: 1,
            minDetectionConfidence: 0.7,
            minTrackingConfidence: 0.7
        });

        handsRef.current.onResults(onResults);

        if (webcamRef.current) {
            cameraRef.current = new camera.Camera(webcamRef.current.video, {
                onFrame: async () => {
                    if (webcamRef.current && handsRef.current) {
                        await handsRef.current.send({
                            image: webcamRef.current.video
                        });
                    }
                },
                width: 640,
                height: 480
            });
            cameraRef.current.start()
                .then(() => setCameraReady(true))
                .catch((err) => console.error("Error starting camera:", err));
        }

        return () => {
            if (cameraRef.current) {
                cameraRef.current.stop();
            }
            if (handsRef.current) {
                handsRef.current.close();
            }
        };
    }, []);

    const onResults = (results) => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.save();
        ctx.scale(-1, 1);
        ctx.translate(-canvas.width, 0);

        if (results.image) {
            ctx.drawImage(results.image, 0, 0, canvas.width, canvas.height);
        }

        if (results.multiHandLandmarks && results.multiHandLandmarks.length > 0) {
            for (const landmarks of results.multiHandLandmarks) {
                drawConnectors(ctx, landmarks, hands.HAND_CONNECTIONS, {
                    color: '#00FF00',
                    lineWidth: 5
                });
                drawLandmarks(ctx, landmarks, {
                    color: '#FF0000',
                    lineWidth: 2
                });

                const mirroredLandmarks = landmarks.map(landmark => ({
                    ...landmark,
                    x: 1 - landmark.x
                }));

                const handAnalysis = analyzeHand(mirroredLandmarks);
                setDebugInfo(handAnalysis);
                
                const recognition = recognizeKodalySign(mirroredLandmarks);
                setDetectedSign(recognition);
            }
        } else {
            setDetectedSign(null);
            setDebugInfo(null);
        }

        ctx.restore();
    };

    return (
        <div className="hand-detection">
            <div className="camera-canvas-container">
                <Webcam
                    ref={webcamRef}
                    style={{ display: 'none' }}
                    width={640}
                    height={480}
                    mirrored={false}
                />
                <canvas
                    ref={canvasRef}
                    width={640}
                    height={480}
                />
                {detectedSign && (
                    <div className="detected-sign" style={{
                        backgroundColor: `rgba(0, 0, 0, ${detectedSign.confidence * 0.9 + 0.1})`
                    }}>
                        <h3>{detectedSign.sign.toUpperCase()}</h3>
                        <p>{kodalySigns[detectedSign.sign].description}</p>
                        <div className="confidence-bar" style={{
                            width: `${detectedSign.confidence * 100}%`
                        }}></div>
                    </div>
                )}
            </div>
            
            <div className="controls">
                <button onClick={() => setShowInstructions(!showInstructions)}>
                    {showInstructions ? 'Hide' : 'Show'} Instructions
                </button>
                <button onClick={() => setShowDebug(!showDebug)}>
                    {showDebug ? 'Hide' : 'Show'} Debug Info
                </button>
            </div>

            {showDebug && debugInfo && (
                <div className="debug-info">
                    <h3>Debug Information</h3>
                    <pre>{JSON.stringify(debugInfo, null, 2)}</pre>
                </div>
            )}

            {!cameraReady && (
                <div className="camera-status">
                    <p>Initializing camera... Please allow camera access when prompted.</p>
                </div>
            )}

            {showInstructions && (
                <div className="instructions">
                    <h2>Kod√°ly Hand Signs Guide</h2>
                    <div className="signs-guide">
                        {Object.entries(kodalySigns).map(([note, data]) => (
                            <div key={note} className="sign-item">
                                <h3>{note.toUpperCase()}</h3>
                                <p>{data.description}</p>
                            </div>
                        ))}
                    </div>
                    <div className="usage-tips">
                        <h3>Tips for Best Recognition:</h3>
                        <ul>
                            <li>Keep your hand clearly visible to the camera</li>
                            <li>Make distinct hand shapes as shown in the guide</li>
                            <li>Maintain proper finger positions</li>
                            <li>Watch the confidence bar for recognition quality</li>
                        </ul>
                    </div>
                </div>
            )}
        </div>
    );
};

export default HandDetection;
