// ... (keep all helper functions the same until the recognizeKodalySign function)

export function recognizeKodalySign(landmarks) {
    if (!landmarks || landmarks.length < 21) return null;

    const fingers = analyzeFingers(landmarks);
    const handDir = getHandDirection(landmarks);
    const palmDir = getPalmDirection(landmarks);
    const thumbDir = getThumbDirection(landmarks);

    // Debug info
    const debug = {
        extensions: fingers.values,
        handDirection: {
            verticalAngle: handDir.verticalAngle,
            isPointingUp: handDir.isPointingUp,
            isPointingDown: handDir.isPointingDown,
            isPointingForward: handDir.isPointingForward,
            isHorizontal: handDir.isHorizontal
        },
        palmOrientation: {
            isDown: palmDir.isDown,
            isUp: palmDir.isUp,
            isSide: palmDir.isSide
        },
        thumbDirection: {
            angle: thumbDir.angle,
            isPointingDown: thumbDir.isPointingDown,
            isPointingUp: thumbDir.isPointingUp
        }
    };

    // Log values for debugging
    console.log('Current hand state:', {
        fingers: fingers.values,
        handAngle: handDir.verticalAngle,
        palmDirection: palmDir,
        thumbDirection: thumbDir
    });

    let sign = null;
    let confidence = 0;

    // DO - Tight fist
    if (fingers.values.thumb < 0.5 &&    
        fingers.values.index < 0.5 &&
        fingers.values.middle < 0.5 &&
        fingers.values.ring < 0.5 &&
        fingers.values.pinky < 0.5) {
        sign = 'do';
        confidence = 0.9;
    }
    
    // RE - Angled up, fingers together
    else if (fingers.values.index > 0.8 &&     
             fingers.values.middle > 0.8 &&
             fingers.values.ring > 0.8 &&
             fingers.values.pinky > 0.8 &&
             handDir.verticalAngle > 20 &&     
             handDir.verticalAngle < 70) {
        sign = 're';
        confidence = 0.9;
    }
    
    // MI - Flat hand horizontal
    else if (fingers.values.index > 0.9 &&      // Almost fully extended
             fingers.values.middle > 0.9 &&
             fingers.values.ring > 0.9 &&
             fingers.values.pinky > 0.9 &&
             Math.abs(handDir.verticalAngle) < 10) {  // Nearly horizontal
        sign = 'mi';
        confidence = 0.9;
    }
    
    // FA - Thumb pointing down, other fingers in fist
    else if (fingers.values.thumb > 0.7 &&      // Extended thumb
             fingers.values.index < 0.5 &&       // Curled fingers
             fingers.values.middle < 0.5 &&
             fingers.values.ring < 0.5 &&
             fingers.values.pinky < 0.5 &&
             thumbDir.angle < -30) {             // Thumb pointing downward
        sign = 'fa';
        confidence = 0.9;
    }
    
    // SOL - Curved fingers to side
    else if (fingers.values.index > 0.6 &&      // Partially extended
             fingers.values.index < 0.9 &&       // But not fully straight
             fingers.values.middle > 0.6 &&
             fingers.values.middle < 0.9 &&
             fingers.values.ring > 0.6 &&
             fingers.values.ring < 0.9 &&
             fingers.values.pinky > 0.6 &&
             fingers.values.pinky < 0.9 &&
             Math.abs(handDir.direction.x) > 0.6) {  // Pointing sideways
        sign = 'sol';
        confidence = 0.9;
    }
    
    // LA - Curved fingers, palm down
    else if (fingers.values.index > 0.5 &&      // Moderately extended
             fingers.values.index < 0.8 &&       // But not straight
             fingers.values.middle > 0.5 &&
             fingers.values.middle < 0.8 &&
             fingers.values.ring > 0.5 &&
             fingers.values.ring < 0.8 &&
             fingers.values.pinky > 0.5 &&
             fingers.values.pinky < 0.8) {
        sign = 'la';
        confidence = 0.9;
    }
    
    // TI - Index up, others closed
    else if (fingers.values.index > 0.8 &&      // Index straight
             fingers.values.middle < 0.5 &&      // Others curled
             fingers.values.ring < 0.5 &&
             fingers.values.pinky < 0.5 &&
             handDir.verticalAngle > 30) {       // Pointing upward
        sign = 'ti';
        confidence = 0.9;
    }

    if (sign) {
        console.log('Detected:', sign, 'with confidence:', confidence);
    }

    return {
        sign,
        confidence,
        debug
    };
}
