// ... (keep all the helper functions the same until the recognizeKodalySign function)

export function recognizeKodalySign(landmarks) {
    if (!landmarks || landmarks.length < 21) return null;

    const fingers = analyzeFingers(landmarks);
    const handDir = getHandDirection(landmarks);
    const palmDir = getPalmDirection(landmarks);

    // Debug info - keep the same debug structure
    const debug = {
        extensions: fingers.values,
        handDirection: {
            verticalAngle: handDir.verticalAngle,
            isPointingUp: handDir.isPointingUp,
            isPointingDown: handDir.isPointingDown,
            isPointingForward: handDir.isPointingForward,
            isHorizontal: handDir.isHorizontal
        },
        palmOrientation: {
            isDown: palmDir.isDown,
            isUp: palmDir.isUp,
            isSide: palmDir.isSide
        }
    };

    let sign = null;
    let confidence = 0;

    // For debugging - log current values
    console.log('Hand state:', {
        fingerValues: fingers.values,
        handAngle: handDir.verticalAngle,
        palmDirection: palmDir
    });

    // DO - Tight fist
    if (fingers.values.thumb < 0.4 &&    // All fingers tightly curled
        fingers.values.index < 0.4 &&
        fingers.values.middle < 0.4 &&
        fingers.values.ring < 0.4 &&
        fingers.values.pinky < 0.4) {
        sign = 'do';
        confidence = 0.9;
    }
    
    // RE - Angled up, fingers together
    else if (fingers.values.index > 0.7 &&     // Fingers straight
             fingers.values.middle > 0.7 &&
             fingers.values.ring > 0.7 &&
             fingers.values.pinky > 0.7 &&
             handDir.verticalAngle > 20 &&     // Angled upward
             handDir.verticalAngle < 70) {     // But not too vertical
        sign = 're';
        confidence = 0.9;
    }
    
    // MI - Completely flat hand
    else if (fingers.values.index > 0.8 &&     // Fingers very straight
             fingers.values.middle > 0.8 &&
             fingers.values.ring > 0.8 &&
             fingers.values.pinky > 0.8 &&
             Math.abs(handDir.verticalAngle) < 20 && // Very horizontal
             palmDir.isDown) {                       // Palm facing down
        sign = 'mi';
        confidence = 0.9;
    }
    
    // FA - Thumb out, others relaxed
    else if (fingers.values.thumb > 0.7 &&     // Thumb clearly out
             fingers.values.index > 0.3 &&     // Other fingers relaxed
             fingers.values.index < 0.7 &&     // but not fully straight
             fingers.values.middle > 0.3 &&     
             fingers.values.middle < 0.7 &&
             fingers.values.ring > 0.3 &&
             fingers.values.ring < 0.7 &&
             fingers.values.pinky > 0.3 &&
             fingers.values.pinky < 0.7) {
        sign = 'fa';
        confidence = 0.9;
    }
    
    // SOL - Forward pointing, palm side
    else if (fingers.values.index > 0.7 &&     // Fingers straight
             fingers.values.middle > 0.7 &&
             fingers.values.ring > 0.7 &&
             fingers.values.pinky > 0.7 &&
             handDir.isPointingForward &&       // Pointing forward
             palmDir.isSide) {                  // Palm to side
        sign = 'sol';
        confidence = 0.9;
    }
    
    // LA - Curved fingers, palm down
    else if (fingers.values.index > 0.4 &&     // Fingers curved
             fingers.values.index < 0.7 &&     // not straight, not closed
             fingers.values.middle > 0.4 &&
             fingers.values.middle < 0.7 &&
             fingers.values.ring > 0.4 &&
             fingers.values.ring < 0.7 &&
             fingers.values.pinky > 0.4 &&
             fingers.values.pinky < 0.7 &&
             palmDir.isDown) {                 // Palm down
        sign = 'la';
        confidence = 0.9;
    }
    
    // TI - Index up, others closed
    else if (fingers.values.index > 0.8 &&     // Index very straight
             fingers.values.middle < 0.4 &&     // Others curled
             fingers.values.ring < 0.4 &&
             fingers.values.pinky < 0.4 &&
             handDir.isPointingUp) {           // Pointing up
        sign = 'ti';
        confidence = 0.9;
    }

    if (sign) {
        console.log('Detected:', sign, 'with confidence:', confidence);
    }

    return {
        sign,
        confidence,
        debug
    };
}
