import React, { useState } from 'react';
import HandDetection from './HandDetection';
import VoiceController from './VoiceController';
import './ComposerApp.css';

function ComposerApp() {
    const [currentComposition, setCurrentComposition] = useState({
        notes: [],
        currentOctave: 4
    });

    const handleCompositionUpdate = (updatedComposition) => {
        setCurrentComposition(updatedComposition);
    };

    return (
        <div className="composer-app">
            <div className="app-container">
                <div className="detection-section">
                    <HandDetection />
                </div>
                
                <div className="voice-section">
                    <VoiceController 
                        currentNote={null} // We'll get this from HandDetection
                        onCompositionUpdate={handleCompositionUpdate}
                    />
                </div>
            </div>
            
            {currentComposition.notes.length > 0 && (
                <div className="composition-display">
                    <h3>Your Composition:</h3>
                    <div className="notes-list">
                        {currentComposition.notes.map((note, index) => (
                            <span key={index} className="composition-note">
                                {note.note.toUpperCase()}{note.octave}
                            </span>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
}

export default ComposerApp;
